package com.syntax.class10;

public class HW {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double[] num = { 2.99, 3.99, 4.99, 5.99 };
		for (double x : num) {
			System.out.print(x + " ");
		}
		System.out.println();
		for (int i = 0; i < num.length; i++) {
			System.out.print(num[i] + " ");
		}

	}

}
