package com.syntax.reviewclass1;

public class OperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Operators in java 
		//Arithmetic Operators= +,-,/,*,%
		// Logical Operators == != < > <= >=
		
		int num=10;
		int num2=10;
		System.out.println(num+num2);
		System.out.println(num>=num2); //num>num2=>f or num=num2=>t f+t=t
		//Whole 0-infinity Negative infinity to positive infinity
		System.out.println(Long.MAX_VALUE);//Maximum Range
		System.out.println(Long.MIN_VALUE);//Maximum range
		System.out.println(Byte.MAX_VALUE);
		System.out.println(Integer.MAX_VALUE);
		System.out.println(num/num2);
		System.out.println(num%num2);
		
		System.out.println(10/3);
		System.out.println(10%3);
		
		System.out.println(10.0/3.0);
		System.out.println((int)10.0/3);
		System.out.println();
		System.out.println();
		
		
		

	}

}
